import { Gauge } from "@ant-design/charts";

interface PieChartProps {
  value: number;
  max: number;
}

export const PieChart = ({ value, max }: PieChartProps = { value: 0, max: 0 }) => {
    const settings = {
        height: 80,
        width: 80,
        percent: value / max,
        range: {
          color: ['#3b82f6', '#e0e0e0'],
        },
        statistic: {
          text: `${value}`,
          style: {
            fontSize: '1.5rem',
            fontWeight: 'bold',
          },
        },
      };

    return(
        <div className="w-20 h-20">
            <Gauge {...settings} />
        </div>
    )
}
