import Link from "next/link";

export const Sidebar = () => {
    return (
      <div className="bg-gray-800 text-white h-screen p-6 flex flex-col space-y-4">
        <Link href="/">
          <a className="font-bold text-lg">Dashboard</a>
        </Link>
        <Link href="/skill-test">
          <a className="hover:text-gray-300">Skill Test</a>
        </Link>
        <Link href="/internship">
          <a className="hover:text-gray-300">Internship</a>
        </Link>
      </div>
    );
  };
  
  // Navbar Component
  export const Navbar = () => {
    return (
      <nav className="bg-gray-900 text-white py-4 px-6 flex justify-between items-center">
        <div className="font-bold text-lg">WhatBytes</div>
        <div className="flex items-center space-x-4">
          <div>Rahil Siddique</div>
          <img src="/avatar.png" alt="Avatar" className="w-8 h-8 rounded-full" />
        </div>
      </nav>
    );
  };
